{
  "metaModels": {
    "worker": { "model": "claude-sonnet-4-5", "thinking": "medium" },
    "general": { "model": "claude-sonnet-4-5", "thinking": "low" },
    "review-general": { "model": "claude-sonnet-4-5", "thinking": "medium" },
    "review-spec": { "model": "claude-sonnet-4-5", "thinking": "high" },
    "review-secop": { "model": "claude-sonnet-4-5", "thinking": "low" }
  },
  "agents": {
    "implement": "worker",
    "review-general": "review-general",
    "reviewer-spec-audit": "review-spec",
    "reviewer-second-opinion": "review-secop",
    "review-merge": "general",
    "fixer": "general"
  },
  "workflow": {
    "enableResearcher": false,
    "researchParallelAgents": 1,
    "enableReviewers": ["reviewer-general", "reviewer-spec-audit", "reviewer-second-opinion"],
    "enableFixer": true,
    "enableCloser": true,
    "enableQualityGate": true,
    "failOn": ["Critical"],
    "knowledgeDir": ".tf/knowledge"
  }
}

