import argparse
import json
import os
from pathlib import Path
from typing import List, Optional


def read_json(path: Path):
    if not path.exists():
        return {}
    try:
        return json.loads(path.read_text(encoding="utf-8"))
    except Exception:
        return {}


def find_project_root() -> Optional[Path]:
    cwd = Path.cwd()
    for parent in [cwd, *cwd.parents]:
        if (parent / ".tf").is_dir():
            return parent
    return None


def resolve_knowledge_dir(project_root: Path) -> Path:
    settings_path = project_root / ".tf/config/settings.json"
    knowledge_dir = ".tf/knowledge"
    if settings_path.exists():
        data = read_json(settings_path)
        workflow = data.get("workflow", {}) if isinstance(data, dict) else {}
        if isinstance(workflow, dict):
            knowledge_dir = workflow.get("knowledgeDir", knowledge_dir)
    path = Path(str(knowledge_dir)).expanduser()
    if not path.is_absolute():
        path = project_root / path
    return path


def list_topics(topics_dir: Path) -> List[Path]:
    topics: List[Path] = []
    for p in sorted(topics_dir.iterdir()):
        if not p.is_dir():
            continue
        if p.name.startswith("seed-") or p.name.startswith("baseline-") or p.name.startswith("plan-"):
            topics.append(p)
    return topics


def parse_backlog(path: Path) -> List[List[str]]:
    if not path.exists():
        return []
    rows = []
    for line in path.read_text(encoding="utf-8").splitlines():
        if not line.strip().startswith("|"):
            continue
        cells = [c.strip() for c in line.strip().strip("|").split("|")]
        if not cells:
            continue
        if cells[0].lower() == "id":
            continue
        if set(cells[0]) == {"-"}:
            continue
        if len(cells) < 2:
            continue
        rows.append(cells)
    return rows


def topic_type(name: str) -> str:
    if name.startswith("seed-"):
        return "seed"
    if name.startswith("baseline-"):
        return "baseline"
    if name.startswith("plan-"):
        return "plan"
    return "topic"


def ticket_summary(rows: List[List[str]]) -> str:
    ids = [r[0] for r in rows if r]
    if not ids:
        return "0"
    if len(ids) <= 3:
        return f"{len(ids)} ({', '.join(ids)})"
    return f"{len(ids)} ({', '.join(ids[:3])}, …)"


def resolve_topic(topics_dir: Path, arg: str) -> Path:
    candidate = Path(arg).expanduser()
    if candidate.exists():
        return candidate
    if not candidate.is_absolute():
        rel = Path.cwd() / candidate
        if rel.exists():
            return rel
    return topics_dir / arg


def run_backlog_ls(topic_arg: Optional[str]) -> int:
    project_root = find_project_root()
    if not project_root:
        print("No .tf directory found. Run in a project with .tf/.")
        return 1

    knowledge_dir = resolve_knowledge_dir(project_root)
    topics_dir = knowledge_dir / "topics"

    if not topics_dir.exists():
        print(f"No topics directory found: {topics_dir}")
        return 0

    if topic_arg:
        topic_path = resolve_topic(topics_dir, topic_arg)
        if not topic_path.exists():
            available = [p.name for p in list_topics(topics_dir)]
            print(f"Topic not found: {topic_arg}")
            if available:
                print("Available topics:")
                for name in available:
                    print(f"- {name}")
            return 1
        topics = [topic_path]
    else:
        topics = list_topics(topics_dir)

    if not topics:
        print(f"No seed/baseline/plan topics found in {topics_dir}")
        return 0

    if len(topics) == 1:
        topic = topics[0]
        backlog_path = topic / "backlog.md"
        rows = parse_backlog(backlog_path)
        print(f"Topic: {topic.name} ({topic_type(topic.name)})")
        if backlog_path.exists():
            print(f"Backlog: yes ({len(rows)} tickets)")
            print("")
            print(f"# Backlog: {topic.name}")
            print("| ID | Title | Est. Hours |")
            print("|----|-------|------------|")
            for row in rows:
                est = row[2] if len(row) > 2 else ""
                print(f"| {row[0]} | {row[1]} | {est} |")
        else:
            print("Backlog: no (unticketed)")
            print(f"Run: /tf-backlog {topic.name}")
        return 0

    print("| Topic | Type | Backlog | Tickets |")
    print("|-------|------|---------|---------|")
    for topic in topics:
        backlog_path = topic / "backlog.md"
        rows = parse_backlog(backlog_path)
        backlog_status = "yes" if backlog_path.exists() else "no"
        tickets = ticket_summary(rows) if backlog_path.exists() else "0"
        print(f"| {topic.name} | {topic_type(topic.name)} | {backlog_status} | {tickets} |")
    return 0


def build_parser() -> argparse.ArgumentParser:
    parser = argparse.ArgumentParser(description="tf new backlog-ls")
    parser.add_argument("topic", nargs="?", help="Topic id or path")
    return parser


def main(argv: Optional[list[str]] = None) -> int:
    parser = build_parser()
    args = parser.parse_args(argv)
    return run_backlog_ls(args.topic)


if __name__ == "__main__":
    raise SystemExit(main())

import sys
from typing import Optional, List

from . import (
    agentsmd_new,
    backlog_ls_new,
    doctor_new,
    init_new,
    login_new,
    next_new,
    ralph_new,
    setup_new,
    sync_new,
    track_new,
    update_new,
)


def usage() -> None:
    print(
        """Ticketflow (new Python CLI)

Usage:
  tf new agentsmd <subcommand> [path]
  tf new backlog-ls [topic-id-or-path]
  tf new doctor [--project <path>] [--fix]
  tf new init [--project <path>]
  tf new login [--project <path>] [--global]
  tf new next [--project <path>]
  tf new ralph <subcommand> [options]
  tf new setup [--project <path>] [--global]
  tf new sync [--project <path>] [--global]
  tf new track <path> [--file <files_changed_path>]
  tf new update [--project <path>] [--global]

Commands:
  agentsmd   AGENTS.md management (init/status/validate/fix/update)
  backlog-ls List backlog status for seed/baseline/plan topics
  doctor     Preflight checks for tk/pi/extensions/checkers
  init       Create .tf project scaffolding
  login      Configure API keys for web search and MCP servers
  next       Print the next ready ticket id
  ralph      Python implementation of Ralph loop (start/run)
  setup      Install Pi/TF assets and extensions
  sync       Sync model frontmatter from config
  track      Append a file to files_changed.txt
  update     Download latest agents/skills/prompts
"""
    )


def main(argv: Optional[List[str]] = None) -> int:
    if argv is None:
        argv = sys.argv[1:]

    if not argv or argv[0] in {"-h", "--help", "help"}:
        usage()
        return 0

    command = argv[0]
    rest = argv[1:]

    if command == "agentsmd":
        return agentsmd_new.main(rest)
    if command == "backlog-ls":
        return backlog_ls_new.main(rest)
    if command == "doctor":
        return doctor_new.main(rest)
    if command == "init":
        return init_new.main(rest)
    if command == "login":
        return login_new.main(rest)
    if command == "next":
        return next_new.main(rest)
    if command == "ralph":
        return ralph_new.main(rest)
    if command == "setup":
        return setup_new.main(rest)
    if command == "sync":
        return sync_new.main(rest)
    if command == "track":
        return track_new.main(rest)
    if command == "update":
        return update_new.main(rest)

    print(f"Unknown 'new' subcommand: {command}", file=sys.stderr)
    usage()
    return 1


if __name__ == "__main__":
    raise SystemExit(main())
